"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[53],{1109:e=>{e.exports=JSON.parse('{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"link","label":"Introduction","href":"/docs/intro","docId":"intro","unlisted":false},{"type":"category","label":"Getting Started","collapsible":true,"collapsed":false,"items":[{"type":"link","label":"Docker","href":"/docs/getting-started/docker","docId":"getting-started/docker","unlisted":false},{"type":"link","label":"Kubernetes (Helm)","href":"/docs/getting-started/helm","docId":"getting-started/helm","unlisted":false}],"href":"/docs/category/getting-started"},{"type":"category","label":"Configuration","collapsible":true,"collapsed":false,"items":[{"type":"link","label":"Server","href":"/docs/configuration/server","docId":"configuration/server","unlisted":false},{"type":"link","label":"Desktop","href":"/docs/configuration/desktop","docId":"configuration/desktop","unlisted":false}],"href":"/docs/category/configuration"},{"type":"category","label":"Guides","collapsible":true,"collapsed":false,"items":[{"type":"link","label":"Session Page","href":"/docs/guides/session","docId":"guides/session","unlisted":false}],"href":"/docs/category/guides"},{"type":"category","label":"Design","collapsible":true,"collapsed":false,"items":[{"type":"link","label":"Streaming","href":"/docs/design/streaming","docId":"design/streaming","unlisted":false},{"type":"link","label":"Input Emulation","href":"/docs/design/input-emulation","docId":"design/input-emulation","unlisted":false},{"type":"link","label":"Peer-to-Peer Connection","href":"/docs/design/peer-to-peer","docId":"design/peer-to-peer","unlisted":false}],"href":"/docs/category/design"}]},"docs":{"configuration/desktop":{"id":"configuration/desktop","title":"Desktop","description":"The Pod Arcade desktop is a containerized application that runs on a virtual \\"desktop\\" and connects to the server. It is responsible for running the games and streaming the video and audio to the client over WebRTC. It also receives input from the server and sends it to the game.","sidebar":"tutorialSidebar"},"configuration/server":{"id":"configuration/server","title":"Server","description":"The Pod Arcade server is an MQTT server that manages the game streaming sessions. Desktops and web browsers connect to this server in order to stream games. The server exposes an HTTP endpoint for serving the web client as well as a websocket endpoint for the MQTT server.","sidebar":"tutorialSidebar"},"design/input-emulation":{"id":"design/input-emulation","title":"Input Emulation","description":"The most crucial part to the Pod Arcade experience is getting input from the Browser to the Desktop. This page explains some of the challenges we faced when designing this system, and what Linux hacks we had to implement to make it work. This page assumes a basic understanding of how Linux handles input devices.","sidebar":"tutorialSidebar"},"design/peer-to-peer":{"id":"design/peer-to-peer","title":"Peer-to-Peer Connection","description":"Having a strong peer-to-peer connection is crucial for a good Pod Arcade experience. This page will explain how Pod Arcade uses WebRTC to establish a peer-to-peer connection between the Desktop and the Browser, and what options you have available to improve your connection.","sidebar":"tutorialSidebar"},"design/streaming":{"id":"design/streaming","title":"Streaming","description":"Pod Arcade uses WebRTC to stream video and audio between the Desktop and the Browser. This page will explain how Pod Arcade uses WebRTC to establish a peer-to-peer connection between the Desktop and the Browser, and what some of the design decisions were that influenced the implementation.","sidebar":"tutorialSidebar"},"getting-started/docker":{"id":"getting-started/docker","title":"Docker","description":"Install Pod Arcade using Docker","sidebar":"tutorialSidebar"},"getting-started/helm":{"id":"getting-started/helm","title":"Kubernetes (Helm)","description":"Install Pod Arcade on Kubernetes using Docker","sidebar":"tutorialSidebar"},"guides/session":{"id":"guides/session","title":"Session Page","description":"Once you have connected to a Desktop, you will be taken to the Session page. This is the primary interface for Pod Arcade and is where you will interact with the desktop.","sidebar":"tutorialSidebar"},"intro":{"id":"intro","title":"Introduction","description":"Pod Arcade is an open-source project that enables you to stream games via RetroArch or other compatible software, running on Wayland, directly to your web browser.","sidebar":"tutorialSidebar"}}}')}}]);